mapper.select:
select ar.id, ar.batch_id, ar.create_time, ar.modify_time, ar.name, ar.engage_code, ar.engage_name, ar.category_id, ar.category_name, ar.lease_term_code, ar.lease_term_name, ar.lease_days, ar.start_time, ar.end_time, ar.state, ar.creator_id, ar.creator, ar.market_id, ar.market_code, ar.mch_id, ar.mch_name, ar.version, ari.assets_id as assetsId, ari.assets_name as assetsName, ari.assets_type as assetsType from assets_rental as ar left join assets_rental_item as ari on ari.assets_rental_id = ar.id
and ari.assets_id = #{assetsId}
and ar.state = #{state}

select ar.id, ar.batch_id, ar.create_time, ar.modify_time, ar.name, ar.engage_code, ar.engage_name, ar.category_id, ar.category_name, ar.lease_term_code, ar.lease_term_name, ar.lease_days, ar.start_time, ar.end_time, ar.state, ar.creator_id, ar.creator, ar.market_id, ar.market_code, ar.mch_id, ar.mch_name, ar.version, ari.assets_id as assetsId, ari.assets_name as assetsName, ari.assets_type as assetsType, ari.first_district_id as firstDistrictId, ari.first_district_name as firstDistrictName, ari.second_district_id as secondDistrictId, ari.second_district_name as secondDistrictName from assets_rental_item as ari left join assets_rental as ar on ari.assets_rental_id = ar.id
and ar.state = #{state}
and ar.batch_id = #{batchId,jdbcType=VARCHAR}
and ar.mch_id = #{mchId,jdbcType=BIGINT}
and ari.assets_name like concat('%',#{keyword},'%')

select ar.id, ar.batch_id, ar.mch_id from assets_rental as ar left join assets_rental_item as ari on ari.assets_rental_id = ar.id
and ari.assets_id in
#{assetsId}
