mapper.select:
SELECT customer_id AS customerId, customer_name AS customerName, certificate_number AS certificateNumber, customer_cellphone AS customerCellphone, type_name AS typeName, assets_name AS assetsName,
SUM(amount - refund_amount - chargeback_amount) AS amount
FROM deposit_order
WHERE 1 = 1 AND (state=4 OR state=5)

  AND customer_name=#{customerName}


  AND customer_cellphone=#{customerCellphone}


  AND certificate_number=#{certificateNumber}


  AND type_code=#{typeCode}


  AND assets_name=#{assetsName}

GROUP BY customer_id, assets_id, assets_type

select count(customerId) from (SELECT customer_id AS customerId, customer_name AS customerName, certificate_number AS certificateNumber, customer_cellphone AS customerCellphone, type_name AS typeName, assets_name AS assetsName,
SUM(amount - refund_amount) AS balance
FROM deposit_order
WHERE 1 = 1 AND (state=4 OR state=5)

  AND customer_name=#{customerName}


  AND customer_cellphone=#{customerCellphone}


  AND certificate_number=#{certificateNumber}


  AND type_code=#{typeCode}


  AND assets_name=#{assetsName}

GROUP BY customerId, assets_id, assets_type
) dep

SELECT SUM(balance) AS balance
FROM(
SELECT customer_id AS customerId, customer_name AS customerName, certificate_number AS certificateNumber, customer_cellphone AS customerCellphone, type_name AS typeName, assets_name AS assetsName,
SUM(amount - refund_amount) AS balance
FROM deposit_order
WHERE 1 = 1 AND (state=4 OR state=5)

  AND customer_name=#{customerName}


  AND customer_cellphone=#{customerCellphone}


  AND certificate_number=#{certificateNumber}


  AND type_code=#{typeCode}


  AND assets_name=#{assetsName}

GROUP BY customer_id, assets_id, assets_type
)dep
