mapper.select:
SELECT a.id AS 'assetsId', od.id AS 'orderItemId', a.`name` AS 'assetsName', o.start_time AS 'startTime', o.end_time AS 'endTime', d1.`name` AS 'firstDistrictName', d2.`name` AS 'secDistrictName', a.number AS 'number', a.unit AS 'unit', a.corner AS 'corner', od.pay_state AS 'payState', od.state AS 'state', b.amount AS 'amount', c.balance AS 'balance', o.`code` AS 'code', o.total_amount AS 'totalAmount', o.paid_amount AS 'paidAmount', od.refund_state AS 'refundState', od.stop_Time AS 'stopTime', od.state AS 'state' FROM `dili-basic-data`.assets a LEFT JOIN assets_lease_order_item od ON od.assets_id=a.id LEFT JOIN assets_lease_order o ON o.id=od.lease_order_id LEFT JOIN `dili-basic-data`.district d1 ON d1.id=a.area LEFT JOIN `dili-basic-data`.district d2 ON d2.id=a.second_area LEFT JOIN ( SELECT o.assets_id,ROUND(SUM(o.amount)/100,2) AS 'amount' FROM `dili-assets`.deposit_order o WHERE 1 = 1 AND o.state NOT IN (2) GROUP BY o.assets_id )b ON b.assets_id=a.id LEFT JOIN ( SELECT b.assets_id,ROUND(SUM(b.balance)/100,2) AS 'balance' FROM `dili-assets`.deposit_balance b WHERE 1 = 1 GROUP BY b.assets_id )c ON c.assets_id=a.id WHERE 1 = 1
AND (o.start_time <= #{searchDate} AND (od.stop_Time>= #{searchDate} OR o.end_time >= #{searchDate})or od.id is null)
AND a.id=#{assetsId}
AND a.area=#{firstDistrictId}
AND a.second_area=#{secDistrictId}
AND a.pay_state=#{payState}
